svg.script:
var svg=document.querySelectorAll("svg");
var st=document.querySelector("path");

st.parentElement.onmouseenter=function(){
        st.style.fill="#333";
        st.style.stroke="#333";
    }

st.parentElement.onmouseleave=function(){
        st.style.fill="#BDBDBD";
        st.style.stroke="transparent";
    }
